}

function mesh_is_visible(vertices) {
    for (var i = 0; i < vertices.length / 4; i++) {
        if (vertices[i * 4 + 0] > -1. && vertices[i * 4 + 0] < 2. &&
            vertices[i * 4 + 1] > -1. && vertices[i * 4 + 1] < 2.)
            return true
    }
    return false
}

