            var to_wealth = money_heights[j + 1][1]/max_transferable

            let trapezoid = [[1. - from_time / 3., current_need, 0, 0],
            [1. - to_time / 3., current_need, 1, 0],
            [1. - to_time / 3., current_need + to_wealth, 1, 1],
            [1. - from_time / 3., current_need + from_wealth, 0, 1]]

            var mesh = mesh_trapezoid(trapezoid);
            if (!mesh_is_visible(trapezoid.flat()))
                mesh = mesh.map((v) => Math.sign(v) * 3.)
            needer.trapezoids.push(mesh)
            vertices.push(mesh);

            if (point_in_triangle(window.mouse_canvas_position, [trapezoid[0], trapezoid[1], trapezoid[2]]) ||
                point_in_triangle(window.mouse_canvas_position, [trapezoid[0], trapezoid[3], trapezoid[2]])) {
                mouse_html += "user" + (name) + " needs " + round_money(money_heights[money_heights.length - 1][1]) + "<br>";
                mouse_under = true;
                window.needer_clicked = function (needer) {
                    return function () {
                        pause();

                        var change_pledge = `
                        <label style="display:inline-block;">Amount:</label>
                        <input id="need_amount" type="number" value="`+ needer.need + `" step="1" style="display:inline-block; width:64px">`

                        show_option("User" + needer.name + ":<br>" + change_pledge +
                            "<button onclick=\"user_change_income_event(\'" + needer.name + "\', parseFloat($(\'#need_amount\').val()))\">Set Income</button>" +
                            "<br><button onclick=\"user_leave_event(\'" + name + "\')\">Make Leave</button>");
                    }
                }(needer)
            }
        }
        current_need += max_height(money_heights)/max_transferable
    }
    if (!mouse_under)
        window.needer_clicked = null;

    return vertices.flat();
}

function total_current_payments(name) {
    if (!window.current_monthly_payments) return 0;
    var total = 0
    window.current_monthly_payments.map((v) => total += (v.from == name || v.to == name) ? v.amount : 0)
    return total;
}
function total_current_payments_from_to(from, to) {
    if (!window.current_monthly_payments) return 0;
    var total = 0
    window.current_monthly_payments.map((v) => total += (v.from == from && v.to == to) ? v.amount : 0)
    return total;
}

function payments_geometry() {
    window.payment_message = ""
    window.todo_payment_trapezoids = [];
    var vertices = []
    var distribution = get_distribution(window.havers.map(h=>{return {name:h.name, amount:h.haveNeed+window.mincome}}), window.needers.map(n=>{return {name:n.name, amount:window.mincome+n.haveNeed}}));
    distribution = distribution.map((v) => {
        v.amount -= total_current_payments_from_to(v.from, v.to)
        return v;
    })

    window.havers.forEach(u=>u.monthly_aggregation=u.monthly_aggregation_start)
    window.needers.forEach(u=>u.monthly_aggregation=u.monthly_aggregation_start)

    var mouse_under = false;
    for (var i = 0; i < distribution.length; i++) {
        var payment = distribution[i]

        var haver = haver_by_name(payment.from)
        var needer = needer_by_name(payment.to)

        if (!haver || !needer) {
            continue
        }

        let trapezoid = [[1. / 3., haver.monthly_aggregation, payment.amount / max_transferable, 0],
        [2. / 3., needer.monthly_aggregation, payment.amount / max_transferable, 0],
        [2. / 3., needer.monthly_aggregation + payment.amount / max_transferable, payment.amount / max_transferable, 1],
        [1. / 3., haver.monthly_aggregation + payment.amount / max_transferable, payment.amount / max_transferable, 1]]


        window.todo_payment_trapezoids.push(mesh_trapezoid(trapezoid))
        vertices.push(mesh_trapezoid(trapezoid));

        if (point_in_triangle(window.mouse_canvas_position, [trapezoid[0], trapezoid[1], trapezoid[2]]) ||
            point_in_triangle(window.mouse_canvas_position, [trapezoid[0], trapezoid[3], trapezoid[2]])) {
            mouse_html += "TODO Payment<br>From: user" + (payment.from) + "<br>To: user" + (payment.to) + "<br>Amount: " + round_money(payment.amount) + "<br> <div style='border-bottom: 1px solid #000'></div>";
            mouse_under = true;

            var make_payment = `<label style="display:inline-block;">Amount:</label>
                                    <input id="payment_amount" type="number" value="`+ payment.amount + `" max="` + payment.amount + `"step="1" style="display:inline-block; width:64px">`
            window.payment_message += (window.payment_message ? "<br>" : "") + "Payment from User" + payment.from + " to User" + payment.to + "<br>" + make_payment +
                "<button onclick=\"$('#options_div').hide(); user_pay_event(\'" + payment.from + "\', \'" + payment.to + "\', parseFloat($('#payment_amount').val()))\">Make Payment</button>"

            window.payment_clicked = function () {
                pause();
                show_option(window.payment_message);
            }
        }

        haver.monthly_aggregation += payment.amount / max_transferable;
        needer.monthly_aggregation += payment.amount / max_transferable;
    }
    if (!mouse_under)
        window.payment_clicked = null;

    return vertices.flat();
}

