function back() {
    pause();
    var dt = (window.get_cycle() ) % 1;
    dt = dt < .125 ? 1+dt : dt;
    function time_shift(delta, current) {
        if(current > 0) {
            var delta_delta = delta / 60.;
            window.time_elapsed -= (delta_delta) *window.time_window*window.time_dilation
            window.setTimeout(time_shift, 1000/60, delta, current-delta_delta)
        }
    }
    time_shift(dt, dt)
}

function forward() {
    pause();
    var dt = 1.-(window.get_cycle() ) % 1;
    function time_shift(delta, current) {
        if(current > 0) {
            var delta_delta = delta / 60.;
            window.time_elapsed += (delta_delta) *window.time_window*window.time_dilation
            window.setTimeout(time_shift, 1000/60, delta, current-delta_delta)
        }
    }
    time_shift(dt, dt)
}
