function max_height(list) {
    return list.reduce((a, v) => {
        return Math.max(a, v[1])
    }, -1E32)
}
function min_height(list) {
    return list.reduce((a, v) => {
        return Math.min(a, v[1])
    }, 1E32)
}

function max_minus_min(list) {
    return max_height(list) - min_height(list);
}

