    if(window.minimize) window.distribution = window.distribution.reverse()
    window.havers = window.group_members ? window.group_members.filter(u=>{return u.haveNeed > 0}) : []
    window.needers = window.group_members ? window.group_members.filter(u=>{return u.haveNeed < 0}) : []

    var aggregation = 0;
    for (var i = 0; i < window.havers.length; i++) {
        var money_heights = window.havers[i].money_heights
        window.havers[i].monthly_aggregation_start = aggregation
        aggregation += max_height(money_heights)/ max_transferable*window.vertical_height_scalar;
        havers[i].money_heights.push([1., havers[i].money_heights[havers[i].money_heights.length-1][1]])
