        mat2 m = rotate(angle);

		a = a*2.-1.; b = b*2.-1.; c = c*2.-1.; d = d*2.-1.;

        a = a*m; b = b*m; c = c*m; d = d*m;
        a *= s; b *= s; c *= s; d *= s;
        a += o; b += o; c += o; d += o;


        /*
        //= a*.5+.5; b = b*.5+.5; c = c*.5+.5; d = d*.5+.5;
		a = a*2.-1.; b = b*2.-1.; c = c*2.-1.; d = d*2.-1.;
		*/

        vec2 a2 = a*vec2(-1., 1.);
        vec2 b2 = b*vec2(-1., 1.);
        vec2 c2 = c*vec2(-1., 1.);
        vec2 d2 = d*vec2(-1., 1.);
        if(p.x > 0.)
        	p = (invBilinear( p, a, b, c, d ));
        else
        	p = (invBilinear( p, a2, b2, c2, d2 ));
    return p;
}

float square(vec2 uv)
{
    vec2 uv0 = rotatePoint(vec3(uv*2.-1., 0.),vec3(0.,0.,1.), time*PI).xy*.5+.5;;

    float f = 1.-smoothstep(0., .05, abs(uv0.y-frequency));
