    float wave = 1.-length(offset);
    return smoothstep(.5-drop, 1., wave);
}
vec2 flower(vec2 p)
{
	p += saw(smooth_floor(time)/PI/GR);
	p *= rotate(time);
	p += saw(smooth_floor(time)/PI/GR);
	float rots = 5.+1./MAX_DIM+smooth_floor(saw(time)*4.);
	float angle = atan(-p.y, -p.x);
	float radius = length(p);
	angle = floor(((angle/PI)*.5+.5)*rots);


	vec2 a = vec2(1., 0.);
	vec2 b = vec2(1., 1./MAX_DIM);
	vec2 c = vec2(0., 1./MAX_DIM);
	vec2 d = vec2(0., -1./MAX_DIM);

	b *= rotate(angle/rots*2.*PI);
	angle += 1.;
	a *= rotate(angle/rots*2.*PI);
	return (invBilinear( p, a, b, c, d ));
}


vec2 spiral(vec2 uv)
{
    float r = log(length(uv)+1.)/2.;
    float theta = atan(uv.y, uv.x)/PI-r*sin(time/E/GR)/PI;
    return vec2(saw(r+time/E/E),
                saw(theta+time/GR/E))*2.-1.;
}

vec3 phase(float map)
{
    return vec3(sin(map),
                sin(4.0*PI/3.0+map),
                sin(2.0*PI/3.0+map))*.5+.5;
}

void main()
{
    vec2 uv = vec2(vPosition.x, 1.-vPosition.y);
    drop = sqrt((abs(frequency)+abs(volume))/2.);

    vec2 uv2 = uv;
    vec2 uv0 = uv.xy*2.-1.;
    uv0.x *= max(iResolution.x/iResolution.y, 1.);
    uv0.y *= max(iResolution.y/iResolution.x, 1.);
	uv = uv0*.5+.5;
