    uv *= scale;
    uv -= scale/2.0;
    uv.x *= max(iResolution.x/iResolution.y, 1.);
    uv.y *= max(iResolution.y/iResolution.x, 1.);
	float m = smoothstep(0.45, .55, saw(time));
    uv.xy = spiral(uv.xy*PI*GR*E)*m+(1.-m)*(uv0);;

	float nature = smoothstep(.45, .55, saw(time))*(1.-m);
	uv =  uv*(1.-nature)+flower(uv0*2.-1.)*nature;

    for(int i = 0; i <= max_iterations; i++)
