function connect() {
    new MicrophoneSample();

    if (window.websocket) try {
        window.websocket.close()
    } catch (e) { }
    window.websocket = new WebSocket((window.location.protocol.endsWith('https:') ? 'wss://' : 'ws://') + window.location.host + '/irc_websocket')
    window.websocket.onclose = () => {
        document.getElementById("challenge-page").style.display = 'none';
        document.getElementById("connect-page").style.display = 'block';
        document.getElementById("terminal-page").style.display = 'none';
    };

    window.challenge_callback = (challenge_image) => {
        document.getElementById('captcha').setAttribute('src', challenge_image)
        document.getElementById("challenge-page").style.display = 'block';
        document.getElementById("connect-page").style.display = 'none';
        document.getElementById("terminal-page").style.display = 'none';
    }
    window.websocket.onopen = (function () {
    });
    window.websocket.onmessage = (function (event) {
        var string = event.data.toString()
        //console.log(string);
        const data = JSON.parse(string);
        if (data.challenge) {
            window.challenge_callback(data.challenge)
        }
        else if (data.server || data.error) {

            document.getElementById("challenge-page").style.display = 'none';
            document.getElementById("connect-page").style.display = 'none';
            document.getElementById("terminal-page").style.display = 'block';
            appendConsole((data.server ? data.server : data.error) + '\n')
        }
        else if (data.channel) {
            appendConsole("The current user list of channel " + data.channel + " is: " + data.users.reduce((acc, user, index) => acc + user.name + (index != data.users.length - 1 ? ", " : ".\n"), ""));
            if (window.spectrum) window.clearInterval(window.spectrum);
            window.spectrum = setInterval(drawSpectrum, 10)
        }
        else if (data.nickname) {
            appendConsole("Your new nickname is now: " + data.nickname + '\n');
            window.nickname = data.nickname;
        }
        else if (data.sayer) {
            //console.log(data.sayer, window.nickname)
            if (data.sayer != window.nickname) {
                try {
                    let a = data.says.split(' ')[0]
                    let b = data.says.split(' ')[1]
                    a = new Float64Array(Array.from(JSON.parse(a)))
                    b = new Float64Array(Array.from(JSON.parse(b)))
                    window.unmixed.push([a, b])
                } catch (e) {
                    console.log(e);
                    appendConsole(data.sayer + " says: " + data.says + "\n");
                }
            }
        }
    });
}

