    console.error('Error getting microphone', e);
};
function drawSpectrum() {
    window.time_array = new Uint8Array(window.fft_size);
    window.time_byte_array = new Uint8Array(window.fft_size);
    window.freq_array = new Float32Array(window.fft_size);
    window.audioAnalyser.getByteTimeDomainData(window.time_array);
    window.audioAnalyser.getByteTimeDomainData(window.time_byte_array);
    window.audioAnalyser.getFloatFrequencyData(window.freq_array);
    if(window.time_array.reduce((a,v)=>a+Math.abs(v-128.)/128.,0)/window.time_array.length > .025){
        console.log("packet sent");
        window.fft.forward(window.time_array)
        window.websocket.send("/say " + JSON.stringify(Array.from(window.fft.real)) + " " + JSON.stringify(Array.from(window.fft.imag)) + '\n');
    }
    if(window.unmixed.length > 0){
      [a, b] = window.unmixed.shift()
      window.network_audio = window.ifft.inverse(a, b).map((v)=>(v-128.)/128.);;
    } else if( window.network_audio) {
      window.network_audio = window.network_audio.map((v)=>v/1.1)
    }

    if(window.network_audio) {
      window.messengerWorkletNode.port.postMessage(window.network_audio)
    }
}

MicrophoneSample.prototype.onStream = function (stream) {


    window.audioContext ? window.audioContext.close() : (() => { })();
    window.audioContext = new (window.AudioContext ||
        window.webkitAudioContext ||
        window.mozAudioContext ||
        window.oAudioContext ||
        window.msAudioContext)();
    window.audioContext.audioWorklet.addModule('MessengerProcessor.js').then(() => {

        window.microphone_input = window.audioContext.createMediaStreamSource(stream);
        window.audioAnalyser = window.audioContext.createAnalyser();
        window.audioAnalyser.fft_size = window.fft_size
        window.audioAnalyser.smoothingTimeConstant = 0.5;
        window.microphone_input.connect(window.audioAnalyser);

        window.fft = new DFT(window.fft_size, window.audioContext.sampleRate);
        window.ifft = new FFT(window.fft_size, window.audioContext.sampleRate);

        // Extends AudioWorkletNode to simplify the cross-thread message posting.
        class MessengerWorkletNode extends AudioWorkletNode {
            constructor(context) {
                super(context, 'messenger-processor');
                this.port.onmessage = this.handleMessage_.bind(this);
            }

            handleMessage_(event) {
                console.log(event.data)
            }
        }

        window.messengerWorkletNode = new MessengerWorkletNode(window.audioContext);
        window.messengerWorkletNode.connect(window.audioContext.destination);
    });
};
